#set a function that cheecks weather it is a pallindorm number or not 

def pallindrom(st): 
    rev = ""

    for i in range(len(st)-1 , -1 , -1): 
            rev += st[i]
        
        
    if rev == st : 
         print("pallindrom")

    else : 
         print("Not pallindrom ")
    
    
pallindrom("Tanjumul")

pallindrom("10001")
               